export default {
    namespaced:true,
    state:{
        tableData:[],
        townData:[],
        villageData:[],
    },
    mutations:{
        setTableData(state,data){
            setTimeout(()=>{
                data.forEach((i,v)=>{
                    if(i.checkStatus==0){
                        i.checkStatus='审核中'
                    }else{
                        i.checkStatus='已通过'
                    }
                    //替换镇
