        width: '100'
      }],
      tempTableData: [],
      currentPage: 1,
      pagesize: 5
    };
  },
  methods: {
    //获取池塘
    getPonds() {
      this.$axios({
        url: '/api/ponds/getAll',
        methods: 'GET'
      }).then(res => {
        if (res.data.status == 200) {
          this.$store.commit('ponds/setPolygons', res.data.data.data);
          this.getAllTown();
          this.getALlVillage();
        }
      });
    },
    //获取所有的镇和村
    getAllTown() {
      this.$axios({
        url: '/api/area/getAllTown',
        method: 'GET'
      }).then(res => {
        if (res.data.status == 200) {
          //赋值  
          this.$store.commit('ponds/setTownData', res.data.data.data);
        }
      });
    },
    getALlVillage() {
      this.$axios({
        url: '/api/area/getAllVillage',
        method: 'GET'
      }).then(res => {
        if (res.data.status == 200) {
          //赋值  
          this.$store.commit('ponds/setVillageData', res.data.data.data);
        }
      });
    }
  },
  created() {
    this.getPonds();
  },
  computed: {
    Polygons() {
      return this.$store.state.ponds.polygons;
    }
  }
};
