import Time from '../../api/time'

export default {
    namespaced:true,
    state:{
        //discussion
        discussionList:[],
        discussionId:'',
        showReply:false,

        DCurrentPage:1,
        DPageSize:5,

        //reply
        replyList:[],
        replyId:'',
        RCurrentPage:1,
        RPageSize:5,

        //userid
        uid:'',
        isAdmin:'',
        name:'',
    },
    mutations:{
        setDiscussionList(state,data){
            data.forEach((i,v)=>{
                i.time=Time.time(i.time)
            })

            state.discussionList=data
        },
        setDid(state,id){
            state.discussionId=id
        },
        setShowReply(state,data){
            state.showReply=data
        },

        //reply
        setReplyList(state,data){
            //给有rid的列中的user添加user信息
            data.forEach((i,v)=>{
                if(i.rid){
                    //再循环
                    data.forEach((subi,subv)=>{
                        //找到父回复
                        if(i.rid==subi.id){
                            i.fatherUser=subi.user
                        }
                    })
                    
                }
                i.time=Time.time(i.time)

            })



            state.replyList=data
        },
        //reply-id
        setRId(state,id){
            state.replyId=id
        },
        

        setUid(state,id){
            state.uid=id
        },
        setAdmin(state,num){
            state.isAdmin=num
        },
        setName(state,str){
            state.name=str
        },


        //page
        setDCurrentPage(state,num){
            state.DCurrentPage=num
        },
        setDPageSize(state,num){
            state.DPageSize=num
        },
        setRCurrentPage(state,num){
            state.RCurrentPage=num
        },
        setRPageSize(state,num){
            state.RPageSize=num
        },
    }

}
