export default {
    namespaced:true,
    state:{
        tableData:[],
        townData:[],
        villageData:[],
    },
    mutations:{
        setTableData(state,data){
            setTimeout()
            data.forEach((i,v)=>{
                if(i.checkStatus==0){
                    i.checkStatus='审核中'
                }else{
                    i.checkStatus='已通过'
                }
                //替换镇
                state.townData.forEach((ti,tv)=>{
                    if(i.town==ti.id){
                        i.town=ti.town
                    }
                })
                //替换村
                state.villageData.forEach((vi,vv)=>{
                    if(i.village==vi.id){
                        i.village=vi.village
                    }
                })
            })

            state.tableData=data
        },
        deleteItem(state,id){
            state.tableData=state.tableData.filter(item=>item.id!=id)
        },
        addItem(state,data){
            data.checkStatus='审核中'
            state.tableData.push(data)
        },

        setTownData(state,data){
            state.townData=data
        },
        setVillageData(state,data){
            state.villageData=data
        },
        
    }

}
