export default {
    namespaced:true,
    state:{
        //discussion
        discussionList:[],
        discussionId:'',
        showReply:false,


        //reply
        
    },
    mutations:{
        setDiscussionList(state,data){
            state.discussionList=data
        },
        setDid(state,id){
            state.discussionId=id
        },
        setShowReply(state,data){
            state.showReply=data
        },
        
    }

}
