        label: '所在区/县',
        width: '150'
      }, {
        'id': '4',
        prop: 'townContent',
        label: '所在镇',
        width: '150'
      }, {
        'id': '5',
        prop: 'villageContent',
        label: '所在村',
        width: '150'
      }, {
        'id': '6',
        prop: 'creditLevel',
        label: '信用等级',
        width: '100'
      }, {
        'id': '7',
        prop: 'departmentId',
        label: '组织机构代码',
        width: '150'
      }, {
        'id': '8',
        prop: 'checkStatus',
        label: '审核状态',
        width: '100'
      }]
    };
  },
  methods: {
    //获取池塘
    getPonds() {
      this.$axios({
        url: '/api/ponds/getAll',
        methods: 'GET'
      }).then(res => {
        if (res.data.status == 200) {
          this.$store.commit('ponds/setPolygons', res.data.data.data);
          this.getAllTown();
          this.getALlVillage();
        }
      });
    },
    //获取所有的镇和村
    getAllTown() {
      this.$axios({
        url: '/api/area/getAllTown',
        method: 'GET'
      }).then(res => {
        if (res.data.status == 200) {
          //赋值  
          this.$store.commit('ponds/setTownData', res.data.data.data);
        }
      });
    },
    getALlVillage() {
      this.$axios({
        url: '/api/area/getAllVillage',
        method: 'GET'
      }).then(res => {
        if (res.data.status == 200) {
          //赋值  
          this.$store.commit('ponds/setVillageData', res.data.data.data);
        }
      });
    }
  },
  created() {
    this.getPonds();
  }
};
