export default {
    namespaced:true,
    state:{
        discussionList:[],
        discussionId:'',
        showReply:false
    },
    mutations:{
        setDiscussionList(state,data){
            state.discussionList=data
        },
        setDid(state,id){
            state.discussionId=id
        }
        
    }

}
