                polygon:[]
            }
            data.forEach((i,v)=>{
                object.lat=i.lat
                object.lng=i.lng
                //对i.polygon做变化
                let tempP=i.polygon.split(';')
                tempP.forEach(subi=>{
                    let lat=subi.split(',')[0]
                    let lng=subi.split(',')[1]
