export default {
    namespaced:true,
    state:{
        //获取的所有池塘
        polygons:[],
        //owner list，渲染的是选择的表
        owners:[],
        //product
        products:[],        

        //form表，对接前端,
        //当form表变化，就要把变化的新值添加到polygons中
        //以后点击这个新的多边形，就根据这个新值显示
        form:{
            number:'',
            //owner
            ownerId:'',
            owner:'',
            //product
            productId:'',
            product:'',
            //经纬度
            lng:'',
            lat:'',
            //面积
            estimateArea:'',
            area:'',
            //town
            town:'',
            village:'',
            polygon:'',
            comment:'',
            checkStatus:0,
            id:'',
        },

        //添加池塘和停止绘制
        addPond:false,
        //编辑池塘和停止编辑
        editPond:false,

        //drawingPolygon
        drawingPolygon:'',

    },
    mutations:{
        //对获取的池塘数据进行处理
        setPolygons(state,data){

            data.forEach((i,v)=>{
                let object={
                    position:{
                        lat:'',
                        lng:'',
                    },
                    polygon:[],
                    owner:{},
                    product:{},
                    area:'',
                    estimateArea:'',
                    town:'',
                    village:'',
                    comment:'',
                    number:'',
                    editing:false,
                    id:i.id,
                }
                object.position.lat=i.lat
                object.position.lng=i.lng
                //对i.polygon做变化
                let tempP=i.polygon.split(';')
                tempP.forEach(subi=>{
                    let lat=subi.split(',')[1]
                    let lng=subi.split(',')[0]
                    let temp_obj={
                        lat:lat,
                        lng:lng
                    }
                    object.polygon.push(temp_obj)
                })
                //对池塘的owner做处理
                object.owner=i.owner

                //对池塘的养殖品种做处理
                object.product=i.product

                object.area=i.area
                object.estimateArea=i.estimateArea
                object.town=i.town
                object.village=i.village
                object.comment=i.comment
                object.number=i.number
                state.polygons.push(object)
            })
        },
        //以下两个方法用于设置所有owners和products
        setOwners(state,data){
            state.owners=data
        },
        setProducts(state,data){
            state.products=data
        },
        //form表
        setForm(state,data){
            //更新form
            state.form=data
            //添加到polygons中
            //将data和上面对象对接
            let object={
                position:{
                    lat:data.lat,
                    lng:data.lng,
                },
                polygon:data.polygon,
                owner:data.owner,
                product:data.product,
                area:data.area,
                estimateArea:data.estimateArea,
                town:data.town,
                village:data.village,
                comment:data.comment,
                number:data.number,
                
                editing:false,
                //id是可以随便设置的
                id:65535,
            }
            if(state.polygons[state.polygons.length-1].id!=object.id){
                //push
                state.polygons.push(object)
            }else{
                //修改
                state.polygons[state.polygons.length-1]=object
            }
            
        },
        //updateform
        //停止绘画和修改图形时更新数据
        updateForm(state,data){
            var obj={
                lat:data.lat,
                lng:position.lng,
                estimateArea:estimateArea,
                polygon:polygon,
            }
            state.form.lat=obj.lat
            state.form.lng=obj.lng
            state.form.estimateArea=obj.estimateArea
            state.form.polygon=obj.polygon

        },
        //添加池塘和停止绘制
        setAddPond(state,data){
            state.addPond=data
        },
        //编辑池塘和停止编辑
        setEditPond(state,data){
            state.editPond=data
        },
        //以下两个函数，是用来设置当前选中的多边形是否可以编辑
        //设置为可以编辑
        setEditing(state,data){
            state.polygons.forEach((i,v)=>{
                if(data.id==i.id){
                    i.editing=data.editing
                }
            })
        },
        //全部设置为不可编辑
        setEditingFalse(state){
            state.polygons.forEach((i,v)=>{
                i.editing=false
            })
        },


        setPolygonCenterMarker(state,data){
            state.polygons.forEach((i,v)=>{
                if(i.id==data.id){
                    i.position.lng=data.lng
                    i.position.lat=data.lat
                }
            })
        },


        setDrawingPolygon(state,data){
            console.log(data);
            let str=''
            data.forEach((i,v)=>{
                str=str.concat(i.lng,',',i.lat,';')
            })
            str=str.slice(0, str.length - 1)
            
            state.drawingPolygon=str
        }

    }

}
