import "core-js/modules/es.array.push.js";
export default {
  name: 'addMap',
  data() {
    return {
      //地图初始配置
      center: {
        lng: 0,
        lat: 0
      },
      zoom: 3,
      marker: {},
      position: {
        lng: 0,
        lat: 0
      },
      draggable: false,
      //infowindow展示
      show: false,
      //需要绘制的图行
      drawingPolygon: {
        editing: false,
        paths: []
      },
      drawingMarker: {},
      //infowindow属性,
      infoPosition: {},
      infoOwner: '',
      infoproduct: '',
      //对地图上已存在的图形进行编辑的属性
      id: '',
      editPond: false
    };
  },
  methods: {
    // 初始化地图参数
    handler({
      BMap,
      map
    }) {
      // console.log(BMap, map)
      this.center.lng = 122.048;
      this.center.lat = 29.9424;
      this.zoom = 16;
      this.position.lng = 122.048;
      this.position.lat = 29.9424;
      this.draggable = true;
    },
    //获取池塘
    getPonds() {
      this.$axios({
        url: '/api/ponds/getAll',
        methods: 'GET'
      }).then(res => {
        console.log(res.data.data);
        if (res.data.status == 200) {
          this.$store.commit('ponds/setPolygons', res.data.data.data);
        }
      });
    },
    //跳转到水产
    goProduct(id) {
      this.$router.push({
        path: `/products/add?id=${id}`
      });
    },
    //计算中心点
    getCenterPoint(path) {
      //添加marker
      //计算中心点
      let lng = 0;
      let lat = 0;
      path.forEach((i, v) => {
        lng = lng + i.lng;
        lat = lat + i.lat;
      });
      lng = lng / path.length;
      lat = lat / path.length;
      let drawingObj = {
        lng: lng,
        lat: lat
      };
      return drawingObj;
    },
    //计算面积
    getArea(path) {
      let Arr = [];
      //把path转化成需要的数组形式,path里现在是对象
      path.forEach((i, v) => {
        let arr = [];
        arr.push(i.lng);
        arr.push(i.lat);
        Arr.push(arr);
      });
      Arr.push([path[0].lng, path[0].lat]);
      var p = this.turf.polygon([Arr]);
      // 平方米
      var area = this.turf.area(p);
      // 亩
      var mu = area / (1000 * 2 / 3);
      return mu;
    },
    infoWindowClose() {
      this.show = false;
      //全部设置为false
      this.$store.commit('ponds/setEditingFalse');
      if (!this.EditPond) {
        //设置当前查看的图形id
        this.id = '';
        this.$store.commit('ponds/setId', '');
      }
    },
    infoWindowOpen(item) {
      console.log(item);
      if (item.id == 65535) {
        //新增的
      }
      this.infoOwner = item.owner;
      this.infoproduct = item.product;
      this.infoPosition = {
        lng: item.position.lng,
        lat: item.position.lat
      };

      //设置form的一系列值，传到store
      this.$store.commit('ponds/setNumber', item.number);
      this.$store.commit('ponds/setOwner', item.owner.name);
      this.$store.commit('ponds/setOwnerId', item.owner.id);
      this.$store.commit('ponds/setProduct', item.product.name);
      this.$store.commit('ponds/setProductId', item.product.id);
      this.$store.commit('ponds/setLng', item.position.lng);
      this.$store.commit('ponds/setLat', item.position.lat);
      this.$store.commit('ponds/setEstimateArea', item.estimateArea);
      this.$store.commit('ponds/setArea', item.area);
      this.$store.commit('ponds/setTown', item.town);
      this.$store.commit('ponds/setVillage', item.village);
      this.$store.commit('ponds/setPolygon', item.polygon);
      this.$store.commit('ponds/setComment', item.comment);
      this.$store.commit('ponds/setCheckStatus', item.checkStatus);
      setTimeout(() => {
        this.show = true;
        //设置当前查看的图形id
        this.id = item.id;
        this.$store.commit('ponds/setId', this.id);
      }, 100);
    },
    //多边形更新函数
    polygonsUpdate(e) {
      if (this.EditPond) {
        let paths = e.target.getPath();
        //中心点
        let position = this.getCenterPoint(paths);
        let estimateArea = this.getArea(paths);
        //更改中心点位置更改面积
        this.$store.commit('ponds/editPolygon', {
          lng: position.lng,
          lat: position.lat,
          estimateArea: estimateArea,
          paths: paths,
          id: this.id
        });
      }
    },
    //以下为画图的三个函数
    syncPolygon(e) {
      if (!this.drawingPolygon.editing) {
        return;
      }
      const {
        paths
      } = this.drawingPolygon;
      if (!paths.length) {
        return;
      }
      const path = paths[paths.length - 1];
      if (!path.length) {
        return;
      }
      if (path.length === 1) {
        path.push(e.point);
      }
      this.$set(path, path.length - 1, e.point);
    },
    newPolygon(e) {
      if (!this.drawingPolygon.editing) {
        return;
      }
      const {
        paths
      } = this.drawingPolygon;
      if (!paths.length) {
        paths.push([]);
      }
      const path = paths[paths.length - 1];
      path.pop();
      if (path.length) {
        paths.push([]);
      }
      //计算出中心点，面积
      var position = this.getCenterPoint(paths[0]);
      this.drawingMarker = position;
      var estimateArea = this.getArea(paths[0]);
      //点的路径
      var polygon = paths[0];
      //把这些值传到store，更新form值
      //同时还要在polygons中查看是否已经新增了一个数组
      //有了就更新值，不然就新增
      this.$store.commit('ponds/setLng', position.lng);
      this.$store.commit('ponds/setLat', position.lat);
      this.$store.commit('ponds/setEstimateArea', estimateArea);
      this.$store.commit('ponds/setPolygon', polygon);
      this.$store.commit('ponds/newPolygon');
      //把新增的多边形path清空
      this.drawingPolygon.paths = [];
      //停止绘制
      this.$store.commit('ponds/setAddPond', false);
    },
    paintPolygon(e) {
      if (!this.drawingPolygon.editing) {
        return;
      }
      const {
        paths
      } = this.drawingPolygon;
      !paths.length && paths.push([]);
      paths[paths.length - 1].push(e.point);
    }
  },
  computed: {
    Polygons() {
      return this.$store.state.ponds.polygons;
    },
    AddPond() {
      return this.$store.state.ponds.addPond;
    },
    EditPond() {
      return this.$store.state.ponds.editPond;
    }
  },
  watch: {
    AddPond(n) {
      this.drawingPolygon.editing = n;
      if (n) {
        this.show = false;
        //如果当前有选中的id
        //还要清空store中的form表
        if (this.id) {
          this.$store.commit('ponds/clearForm');
        }
      }
    },
    EditPond(n) {
      this.editPond = n;
      if (n) {
        console.log(n, '编辑模式');
        this.show = false;
        setTimeout(() => {
          this.$store.commit('ponds/setEditing', {
            id: this.id,
            editing: true
          });
        }, 100);
      } else {
        console.log(n, '停止编辑模式');
        //全部设置为false
        this.$store.commit('ponds/setEditingFalse');
      }
    }
  },
  created() {
    this.getPonds();
  }
};
