export default {
    namespaced:true,
    state:{
        userData:[],
        countyData:[
            {
                id: 1,
                county: '定海区'
            }, 
            {
                id: 2,
                county: '普陀区'
            }, 
            {
                id: 3,
                county: '岱山县'
            }, 
            {
                id: 4,
                county: '嵊泗县'
            }, 
        ],
        townData:[],
        villageData:[],
        productData:[],
        ownerData:[],
        //当前查看的字段
        checkingData:{}
    },
    mutations:{
        setUserData(state,data){
            state.userData=data
        },
        setProductData(state,data){
            state.productData=data
        },
        setOwnerData(state,data){
            state.ownerData=data
        },
        setTownData(state,data){
            if(data){
                localStorage.setItem('town',JSON.stringify(data))
                state.townData=data
            }else{
                state.townData=JSON.parse(localStorage.getItem('town'))
            }
        },
        setVillageData(state,data){
            if(data){
                localStorage.setItem('village',JSON.stringify(data))
                state.villageData=data
            }else{
                state.villageData=JSON.parse(localStorage.getItem('village'))
            }
        },
        setCheckingData(state,data){

            //对data做处理
            let obj={
                //改id用于修改dialog表的checkStatus
                id:data.id,
                //用于表示应该区哪个表，修改checkStatus
                op_obj:data.op_obj,
                //用于表示应该被修改的那个表的id
                op_obj_id:''
            }
            if(obj.op_obj=='池塘信息'){
                obj.op_obj=0
            }else if(obj.op_obj=='拥有者信息'){
                obj.op_obj=1
            }else if(obj.op_obj=='水产品信息'){
                obj.op_obj=2
            }
            let op_obj_id=''
            if(data.new_obj){
                op_obj_id=JSON.parse(data.new_obj).id
            }else if(data.old_obj){
                op_obj_id=JSON.parse(data.old_obj).id
            }
            state.checkingData=obj
        }
        
    }

}
