export default {
    namespaced:true,
    state:{
        //discussion
        discussionList:[],
        discussionId:'',
        showReply:false,


        //reply
        replyList:[],
        replyId:'',

        //userid
        uid:''
    },
    mutations:{
        setDiscussionList(state,data){
            state.discussionList=data
        },
        setDid(state,id){
            state.discussionId=id
        },
        setShowReply(state,data){
            state.showReply=data
        },

        //reply
        setReplyList(state,data){
            //给有rid的列中的user添加user信息
            data.forEach((i,v)=>{
                if(i.rid){
                    //再循环
                    data.forEach((subi,subv)=>{
                        //找到父回复
                        if(i.rid==subi.id){
                            i.fatherUser=subi.user
                        }
                    })
                    
                }
            })



            state.replyList=data
        },
        //reply-id
        setRId(state,id){
            state.replyId=id
        },
        

        setUid(state,id){
            state.uid=id
        }
    }

}
