export default {
    namespaced:true,
    state:{
        polygons:[],
        

        //是否显示绘制按钮
        //0=>添加池塘,1=>停止绘制
        drawing:0,
        editingPonds:false,


        //owner list，渲染的是选择的表
        owners:[],
        //product
        products:[],

        //新添加的图形的中心点经纬度
        marker:{},
        //area
        estimateArea:'',
        area:"",
        //drawingPolygon
        drawingPolygon:'',
        //town/village
        town:'',
        village:'',
        comment:'',
        //owner，渲染点击点后的情况
        ownerMarker:'',
        productMarker:'',
        number:'',
        id:""
    },
    mutations:{
        //对获取的池塘数据进行处理
        setPolygons(state,data){

            data.forEach((i,v)=>{
                let object={
                    position:{
                        lat:'',
                        lng:'',
                    },
                    polygon:[],
                    owner:{},
                    product:{},
                    area:'',
                    estimateArea:'',
                    town:'',
                    village:'',
                    comment:'',
                    number:'',
                    editing:false,
                    id:i.id,
                }
                object.position.lat=i.lat
                object.position.lng=i.lng
                //对i.polygon做变化
                let tempP=i.polygon.split(';')
                tempP.forEach(subi=>{
                    let lat=subi.split(',')[1]
                    let lng=subi.split(',')[0]
                    let temp_obj={
                        lat:lat,
                        lng:lng
                    }
                    object.polygon.push(temp_obj)
                })
                //对池塘的owner做处理
                object.owner=i.owner

                //对池塘的养殖品种做处理
                object.product=i.product

                object.area=i.area
                object.estimateArea=i.estimateArea
                object.town=i.town
                object.village=i.village
                object.comment=i.comment
                object.number=i.number
                state.polygons.push(object)
            })
        },

        setEditing(state,data){
            state.polygons.forEach((i,v)=>{
                if(data.id==i.id){
                    i.editing=data.editing
                }
            })
        },
        setEditingFalse(state){
            state.polygons.forEach((i,v)=>{
                i.editing=false
            })
        },
        
        setDrawing(state,data){
            state.drawing=data
        },
        setEditingPonds(state,data){
            state.editingPonds=data
        },

        //yi'xia
        setOwners(state,data){
            state.owners=data
        },
        setProducts(state,data){
            state.products=data
        },

        setId(state,data){
            state.id=data
        },

        setMarker(state,data){
            state.marker=data
        },

        setPolygonCenterMarker(state,data){
            state.polygons.forEach((i,v)=>{
                if(i.id==data.id){
                    i.position.lng=data.lng
                    i.position.lat=data.lat
                }
            })
        },

        setEstimateArea(state,data){
            state.estimateArea=data
        },
        setArea(state,data){
            state.area=data
        },
        setTown(state,data){
            state.town=data
        },
        setVillage(state,data){
            state.village=data
        },
        setComment(state,data){
            state.comment=data
        },
        setOwnerMarker(state,data){
            state.ownerMarker=data
        },
        setProductMarker(state,data){
            state.productMarker=data
        },
        setNumber(state,data){
            state.number=data
        },
        setDrawingPolygon(state,data){
            console.log(data);
            let str=''
            data.forEach((i,v)=>{
                str=str.concat(i.lng,',',i.lat,';')
            })
            str=str.slice(0, str.length - 1)
            
            state.drawingPolygon=str
        }

    }

}
