export default {
  data() {
    return {
      form: {
        title: '',
        content: ''
      },
      dialogVisible: false,
      title: '添加新讨论',
      buttonTitle: '发起新讨论',
      rid: ''
    };
  },
  methods: {
    open() {
      this.dialogVisible = true;
    },
    close() {
      this.dialogVisible = false;
      this.$store.commit('discussion/setRId', '');
    },
    submit() {
      let data = {
        title: this.form.title,
        content: this.form.content,
        time: new Date(),
        uid: this.Uid,
        did: this.Did,
        rid: this.rid
      };
      data = this.qs.stringify(data);
      if (this.ShowReply) {
        this.$store.commit('discussion/setRId', '');
        this.$axios({
          url: '/api/reply/add',
          method: "POST",
          data: data
        }).then(res => {
          console.log(res.data);
          if (res.data.status == 200) {
            this.dialogVisible = false;
            this.$message.success('回复成功');
          }
        });
      } else {
        this.$axios({
          url: '/api/discussion/add',
          method: "POST",
          data: data
        }).then(res => {
          console.log(res.data);
          if (res.data.status == 200) {
            this.dialogVisible = false;
            this.$message.success('发起讨论成功');
            //
          }
        });
      }
    },

    //建立讨论连接
    //port:3001
    ConnectWebSocket() {
      this.websocket = new WebSocket("ws://127.0.0.1:3001");
      // 建立连接
      this.websocket.onopen = () => {
        // 发送数据
        this.websocket.send('发送数据中');
        console.log("websocket发送数据中");
      };
      // 客户端接收服务端返回的数据
      this.websocket.onmessage = evt => {
        console.log("websocket返回的数据：", evt);
        // this.data.push(evt.data)
      };
      // 发生错误时
      this.websocket.onerror = evt => {
        console.log("websocket错误：", evt);
      };
      // 关闭连接
      this.websocket.onclose = evt => {
        console.log("websocket关闭：", evt);
      };
    },
    send() {
      this.websocket.send('inputValue');
    }
  },
  created() {},
  mounted() {},
  computed: {
    ShowReply() {
      return this.$store.state.discussion.showReply;
    },
    Did() {
      return this.$store.state.discussion.discussionId;
    },
    Rid() {
      return this.$store.state.discussion.replyId;
    },
    Uid() {
      return this.$store.state.discussion.uid;
    }
  },
  watch: {
    ShowReply(n, o) {
      if (n) {
        this.title = '发送回复';
        this.buttonTitle = '发送回复';
      } else {
        this.title = '添加新讨论';
        this.buttonTitle = '发起新讨论';
      }
    },
    Rid(n, o) {
      this.rid = this.Rid;
      if (this.rid !== '') {
        this.dialogVisible = true;
      }
    }
  }
};
