export default {
    namespaced:true,
    state:{
        polygons:[],
        

        //是否显示绘制按钮
        //0=>添加池塘,1=>停止绘制,2=>编辑池塘,3=>停止编辑
        drawing:0,


        //owner
        owner:[],

        //product
        product:[],

        //新添加的图形的中心点经纬度
        marker:{},
        //area
        estimateArea:'',
        //drawingPolygon
        drawingPolygon:'',
    },
    mutations:{
        
        setPolygons(state,data){

            data.forEach((i,v)=>{
                let object={
                    position:{
                        lat:'',
                        lng:'',
                    },
                    polygon:[],
                    owner:{},
                    product:{},
                    area:'',
                    estimateArea
                }
                object.position.lat=i.lat
                object.position.lng=i.lng
                //对i.polygon做变化
                let tempP=i.polygon.split(';')
                tempP.forEach(subi=>{
                    let lat=subi.split(',')[1]
                    let lng=subi.split(',')[0]
                    let temp_obj={
                        lat:lat,
                        lng:lng
                    }
                    object.polygon.push(temp_obj)
                })
                //对池塘的owner做处理
                object.owner=i.owner

                //对池塘的养殖品种做处理
                object.product=i.product
                state.polygons.push(object)
            })
        },
        
        setDrawing(state,data){
            state.drawing=data
        },


        setOwner(state,data){
            state.owner=data
        },
        setProduct(state,data){
            state.product=data
        },

        setMarker(state,data){
            state.marker=data
        },
        setEstimateArea(state,data){
            state.estimateArea=data
        },
        setDrawingPolygon(state,data){
            console.log(data);
            let str=''
            data.forEach((i,v)=>{
                str=str.concat(i.lng,',',i.lat,';')
            })
            state.drawingPolygon=str
        }

    }

}
