import "core-js/modules/es.array.push.js";
export default {
  name: "addMap",
  data() {
    return {
      //地图初始配置
      center: {
        lng: 0,
        lat: 0
      },
      zoom: 3,
      marker: {},
      position: {
        lng: 0,
        lat: 0
      },
      draggable: false,
      show: false,
      //需要绘制的图行
      drawingPolygon: {
        editing: false,
        paths: []
      },
      drawingMarker: {},
      drawingEditing: false,
      //infowindow
      infoPosition: {},
      poly: [
        // {
        //     lng:29.9424,
        //     lat:122.048,
        //     polygon:[
        //         {lng: '122.0452438546', lat: '29.94264534284173'},
        //         {lng: '122.0457689541', lat: '29.942290903455056'},
        //         {lng: '122.0448565938', lat: '29.940878850006936'},
        //         {lng: '122.0442264745', lat: '29.94178210920206'},
        //     ],
        // }
      ]
    };
  },
  methods: {
    toggle(name) {
      this[name].editing = !this[name].editing;
    },
    drawEdit() {
      this.drawingEditing = true;
    },
    polygonUpdate(e) {
      this.drawingPolygon.paths[0] = e.target.getPath();
    },
    paintPolygon(e) {
      if (!this.drawingPolygon.editing) {
        return;
      }
      const {
        paths
      } = this.drawingPolygon;
      !paths.length && paths.push([]);
      paths[paths.length - 1].push(e.point);
    },
    syncPolygon(e) {
      if (!this.drawingPolygon.editing) {
        return;
      }
      const {
        paths
      } = this.drawingPolygon;
      if (!paths.length) {
        return;
      }
      const path = paths[paths.length - 1];
      if (!path.length) {
        return;
      }
      if (path.length === 1) {
        path.push(e.point);
      }
      this.$set(path, path.length - 1, e.point);
    },
    newPolygon(e) {
      if (!this.drawingPolygon.editing) {
        return;
      }
      const {
        paths
      } = this.drawingPolygon;
      if (!paths.length) {
        paths.push([]);
      }
      const path = paths[paths.length - 1];
      path.pop();
      if (path.length) {
        paths.push([]);
      }
      // this.getCenterPoint()
      // this.getArea()
      //此后为编辑模式
      this.$store.commit('ponds/setDrawing', 2);
    },
    handler({
      BMap,
      map
    }) {
      // console.log(BMap, map)
      this.center.lng = 122.048;
      this.center.lat = 29.9424;
      this.zoom = 16;
      this.position.lng = 122.048;
      this.position.lat = 29.9424;
      this.draggable = true;
      this.poly = this.$store.state.ponds.polygons;
    },
    infoWindowClose() {
      this.show = false;
    },
    infoWindowOpen(item) {
      console.log(item.position);
      this.infoPosition = {
        lng: item.position.lng
      };
      this.show = true;
    },
    //获取池塘
    getPonds() {
      this.$axios({
        url: '/api/ponds/getAll',
        methods: 'GET'
      }).then(res => {
        console.log(res.data.data);
        if (res.data.status == 200) {
          this.$store.commit('ponds/setPolygons', res.data.data.data);
        }
      });
    },
    goProduct(id) {
      // console.log(id);
      this.$router.push({
        path: `/products/add?id=${id}`
      });
    },
    //计算中心点
    getCenterPoint() {
      const {
        paths
      } = this.drawingPolygon;
      //添加marker
      //计算中心点
      let lng = 0;
      let lat = 0;
      paths[0].forEach((i, v) => {
        lng = lng + i.lng;
        lat = lat + i.lat;
      });
      lng = lng / paths[0].length;
      lat = lat / paths[0].length;
      let drawingObj = {
        lng: lng,
        lat: lat
      };
      this.drawingMarker = drawingObj;
      this.$store.commit('ponds/setMarker', this.drawingMarker);
    },
    //计算面积
    getArea() {
      let paths = this.drawingPolygon.paths[0];
      let Arr = [];
      //把paths转化成需要的数组形式,paths里现在是对象
      paths.forEach((i, v) => {
        let arr = [];
        arr.push(i.lng);
        arr.push(i.lat);
        Arr.push(arr);
      });
      Arr.push([paths[0].lng, paths[0].lat]);
      var p = this.turf.polygon([Arr]);
      // 平方米
      var area = this.turf.area(p);
      // 亩
      var mu = area / (1000 * 2 / 3);
      //store
      this.$store.commit('ponds/setEstimateArea', mu);
    },
    //新池塘上传store
    getDrawingPolygon() {
      this.$store.commit('ponds/setDrawingPolygon', this.drawingPolygon.paths[0]);
    }
  },
  created() {
    this.getPonds();
  },
  mounted() {},
  computed: {
    Drawing() {
      return this.$store.state.ponds.drawing;
    }
  },
  watch: {
    Drawing(n, o) {
      if (n == 0) {
        this.drawingPolygon.editing = false;
      } else if (n == 1) {
        this.drawingPolygon.editing = true;
      } else if (n == 2) {
        this.drawingEditing = false;
        this.drawingPolygon.editing = false;
        //计算中心点
        this.getCenterPoint();
        //计算面积
        this.getArea();
        //新池塘上传store
        this.getDrawingPolygon();
      } else if (n == 3) {
        this.drawingEditing = true;
        this.drawingPolygon.editing = false;
      }
    }
  }
};
