import time from '../../api/time';
export default {
  name: "Main",
  data() {
    return {
      tableItem: [{
        'id': '1',
        prop: 'uid',
        label: '提交人',
        width: '150'
      }, {
        'id': '2',
        prop: 'type',
        label: '类型',
        width: '100'
      }, {
        'id': '3',
        prop: 'op_obj',
        label: '操作对象',
        width: '150'
      }, {
        'id': '4',
        prop: 'checkStatus',
        label: '审核情况',
        width: '100'
      }, {
        'id': '5',
        prop: 'checkResult',
        label: '审核结果',
        width: '200'
      }, {
        'id': '6',
        prop: 'time',
        label: '时间',
        width: '200'
      }],
      tempTableData: [],
      currentPage: 1,
      pagesize: 10,
      length: 0
    };
  },
  methods: {
    getDialog() {
      let data = this.qs.stringify({
        front: (this.currentPage - 1) * this.pagesize,
        end: this.currentPage * this.pagesize
      });
      this.$axios({
        url: '/api/dialog/getAll',
        method: 'POST',
        data: data
      }).then(res => {
        // console.log(res.data);
        if (res.data.status == 200) {
          this.tempTableData = res.data.data.data;
          this.tempTableData.forEach((i, v) => {
            i.time = time.time(i.time);
            //类型
            if (i.type == 0) {
              i.type = '添加';
            } else if (i.type == 1) {
              i.type = '修改';
            } else if (i.type == 2) {
              i.type = '删除';
            }
            //操作对象
            if (i.op_obj == 0) {
              i.op_obj = '池塘信息';
            } else if (i.op_obj == 1) {
              i.op_obj = '拥有者信息';
            } else if (i.op_obj == 2) {
              i.op_obj = '水产品信息';
            }
            if (i.checkStatus == 0) {
              i.checkStatus = '未审核';
            } else if (i.checkStatus == 1) {
              i.checkStatus = '已通过';
            } else if (i.checkStatus == 2) {
              i.checkStatus = '未通过';
            }
            this.UserData.forEach((ui, uv) => {
              if (ui.id == i.uid) {
                i.uid = ui.name;
              }
            });
          });
        }
      });
    },
    getLength() {
      this.$axios({
        url: '/api/dialog/getLength',
        method: 'GET'
      }).then(res => {
        this.length = res.data.data.data.length;
        if (res.data.status == 200) {
          this.getDialog();
        }
      });
    },
    getUser() {
      this.$axios({
        url: '/api/user/getAll',
        method: 'GET'
      }).then(res => {
        console.log(res.data);
        if (res.data.status == 200) {
          this.$store.commit('dialog/setUserData', res.data.data.data);
          this.getLength();
        }
      });
    },
    handleSizeChange(size) {
      this.pagesize = size;
      this.getDialog();
    },
    handleCurrentChange(currentPage) {
      this.currentPage = currentPage;
      this.getDialog();
    },
    check(item) {
      console.log(item);
    }
  },
  created() {
    this.getUser();
  },
  computed: {
    UserData() {
      return this.$store.state.dialog.userData;
    }
  }
};
