import "core-js/modules/es.array.push.js";
export default {
  name: 'addMap',
  data() {
    return {
      //地图初始配置
      center: {
        lng: 0,
        lat: 0
      },
      zoom: 3,
      marker: {},
      position: {
        lng: 0,
        lat: 0
      },
      draggable: false,
      //infowindow展示
      show: false,
      //需要绘制的图行
      drawingPolygon: {
        editing: false,
        paths: []
      },
      drawingMarker: {},
      //infowindow
      infoPosition: {},
      infoOwner: '',
      infoproduct: '',
      id: '',
      polyline: {
        editing: false,
        paths: []
      }
    };
  },
  methods: {
    // 初始化地图参数
    handler({
      BMap,
      map
    }) {
      // console.log(BMap, map)
      this.center.lng = 122.048;
      this.center.lat = 29.9424;
      this.zoom = 16;
      this.position.lng = 122.048;
      this.position.lat = 29.9424;
      this.draggable = true;
    },
    //获取池塘
    getPonds() {
      this.$axios({
        url: '/api/ponds/getAll',
        methods: 'GET'
      }).then(res => {
        console.log(res.data.data);
        if (res.data.status == 200) {
          this.$store.commit('ponds/setPolygons', res.data.data.data);
        }
      });
    },
    //跳转到水产
    goProduct(id) {
      this.$router.push({
        path: `/products/add?id=${id}`
      });
    },
    paintPolygon(e) {
      if (!this.drawingPolygon.editing) {
        return;
      }
      const {
        paths
      } = this.drawingPolygon;
      !paths.length && paths.push([]);
      paths[paths.length - 1].push(e.point);
    },
    syncPolygon(e) {
      if (!this.drawingPolygon.editing) {
        return;
      }
      const {
        paths
      } = this.drawingPolygon;
      if (!paths.length) {
        return;
      }
      const path = paths[paths.length - 1];
      if (!path.length) {
        return;
      }
      if (path.length === 1) {
        path.push(e.point);
      }
      this.$set(path, path.length - 1, e.point);
    },
    newPolygon(e) {
      if (!this.drawingPolygon.editing) {
        return;
      }
      const {
        paths
      } = this.drawingPolygon;
      if (!paths.length) {
        paths.push([]);
      }
      const path = paths[paths.length - 1];
      path.pop();
      if (path.length) {
        paths.push([]);
      }
      // this.getCenterPoint()
      // this.getArea()
    },

    //计算中心点
    getCenterPoint() {
      const {
        paths
      } = this.drawingPolygon;
      //添加marker
      //计算中心点
      let lng = 0;
      let lat = 0;
      paths[0].forEach((i, v) => {
        lng = lng + i.lng;
        lat = lat + i.lat;
      });
      lng = lng / paths[0].length;
      lat = lat / paths[0].length;
      let drawingObj = {
        lng: lng,
        lat: lat
      };
      this.drawingMarker = drawingObj;
      this.$store.commit('ponds/setMarker', this.drawingMarker);
    },
    //计算面积
    getArea() {
      let paths = this.drawingPolygon.paths[0];
      let Arr = [];
      //把paths转化成需要的数组形式,paths里现在是对象
      paths.forEach((i, v) => {
        let arr = [];
        arr.push(i.lng);
        arr.push(i.lat);
        Arr.push(arr);
      });
      Arr.push([paths[0].lng, paths[0].lat]);
      var p = this.turf.polygon([Arr]);
      // 平方米
      var area = this.turf.area(p);
      // 亩
      var mu = area / (1000 * 2 / 3);
      //store
      this.$store.commit('ponds/setEstimateArea', mu);
    },
    infoWindowClose() {
      this.show = false;
      //全部设置为false
      this.$store.commit('ponds/setEditingFalse');
      if (!this.EditingPonds) {
        this.$store.commit('ponds/setId', '');
      }
    },
    infoWindowOpen(item) {
      this.infoPosition = {
        lng: item.position.lng,
        lat: item.position.lat
      };
      this.infoOwner = item.owner;
      this.infoproduct = item.product;
      this.id = item.id;
      setTimeout(() => {
        this.show = true;
        this.$store.commit('ponds/setId', item.id);
      }, 100);
      this.$store.commit('ponds/setMarker', item.position);
      this.$store.commit('ponds/setEstimateArea', item.estimateArea);
      this.$store.commit('ponds/setArea', item.area);
      this.$store.commit('ponds/setTown', item.town);
      this.$store.commit('ponds/setVillage', item.village);
      this.$store.commit('ponds/setComment', item.comment);
      this.$store.commit('ponds/setNumber', item.number);

      //owner/product
      this.$store.commit('ponds/setOwnerMarker', item.owner);
      this.$store.commit('ponds/setProductMarker', item.product);
    },
    polygonUpdate(e) {
      this.drawingPolygon.paths[0] = e.target.getPath();
    },
    polygonsUpdate(e) {
      if (this.EditingPonds) {
        let paths = e.target.getPath();
        this.$store.commit('ponds/setDrawingPolygon', paths);
        //中心点
        let lng = 0;
        let lat = 0;
        paths.forEach((i, v) => {
          lng = lng + i.lng;
          lat = lat + i.lat;
        });
        lng = lng / paths.length;
        lat = lat / paths.length;
        this.$store.commit('ponds/setMarker', {
          lng: lng,
          lat: lat
        });
        //更改中心点位置
        this.$store.commit('ponds/setPolygonCenterMarker', {
          lng: lng,
          lat: lat,
          id: this.id
        });

        //面积
        let Arr = [];
        //把paths转化成需要的数组形式,paths里现在是对象
        paths.forEach((i, v) => {
          let arr = [];
          arr.push(i.lng);
          arr.push(i.lat);
          Arr.push(arr);
        });
        Arr.push([paths[0].lng, paths[0].lat]);
        var p = this.turf.polygon([Arr]);
        // 平方米
        var area = this.turf.area(p);
        // 亩
        var mu = area / (1000 * 2 / 3);
        //store
        this.$store.commit('ponds/setEstimateArea', mu);
      }
    }
  },
  computed: {
    Polygons() {
      return this.$store.state.ponds.polygons;
    },
    AddPond() {
      return this.$store.state.ponds.addPond;
    },
    EditPond() {
      return this.$store.state.ponds.editPond;
    }
  },
  watch: {
    AddPond(n) {
      this.drawingPolygon.editing = n;
    }
  },
  created() {
    this.getPonds();
  }
};
