export default {
    namespaced:true,
    state:{
        tableData:[],
        TownData:[],

    },
    mutations:{
        setTableData(state,data){

            data.forEach((i,v)=>{
                if(i.checkStatus==0){
                    i.checkStatus='审核中'
                }else{
                    i.checkStatus='已通过'
                }
            })

            state.tableData=data
        },
        deleteItem(state,id){
            state.tableData=state.tableData.filter(item=>item.id!=id)
        },
        addItem(state,data){
            data.checkStatus='审核中'
            state.tableData.push(data)
        }
        
    }

}
